d3.select("#screen-button").on("click",function(){network_config.screen_state=!network_config.screen_state;network_config.screen_state===true?enterFullScreen():exitFullScreen();d3.select("#screen-switch").attr("class",network_config.screen_state===true?"fa fa-compress":"fa fa-expand")});function enterFullScreen(){var e=document.documentElement;if(e.requestFullscreen){e.requestFullscreen()}else if(e.mozRequestFullScreen){e.mozRequestFullScreen()}else if(e.webkitRequestFullScreen){e.webkitRequestFullScreen()}}function exitFullScreen(){var e=document;if(e.exitFullscreen){e.exitFullscreen()}else if(e.mozCancelFullScreen){e.mozCancelFullScreen()}else if(e.webkitCancelFullScreen){e.webkitCancelFullScreen()}}